import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import FormAnalytics from './FormAnalytics';

function AdminDashboard() {
  const [newForm, setNewForm] = useState({ title: '', description: '', questions: [] });
  const [forms, setForms] = useState([]);
  const [selectedFormId, setSelectedFormId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchAdminForms();
  }, []);

  const fetchAdminForms = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/forms/admin/myforms', {
        headers: { 'x-auth-token': token }
      });
      setForms(res.data);
    } catch (err) {
      console.error(err.response.data);
    }
  };

  const handleCreateForm = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/forms', newForm, {
        headers: { 'x-auth-token': token }
      });
      alert('Form created successfully!');
      setNewForm({ title: '', description: '', questions: [] });
      fetchAdminForms(); // Refresh the list of forms
    } catch (err) {
      console.error(err.response.data);
      alert('Failed to create form.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/admin/login');
  };

  const handleAddQuestion = () => {
    setNewForm({
      ...newForm,
      questions: [
        ...newForm.questions,
        { questionText: '', questionType: 'text', options: [] }
      ]
    });
  };

  const handleQuestionChange = (index, e) => {
    const { name, value } = e.target;
    const questions = [...newForm.questions];
    questions[index][name] = value;
    setNewForm({ ...newForm, questions });
  };

  const handleOptionsChange = (index, e) => {
    const { value } = e.target;
    const questions = [...newForm.questions];
    questions[index].options = value.split(',').map(opt => opt.trim());
    setNewForm({ ...newForm, questions });
  };

  return (
    <div className="container">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <button onClick={handleLogout} className="btn btn-secondary">Logout</button>
      </div>

      <div className="dashboard-section">
        <h3>Create New Form</h3>
        <form onSubmit={handleCreateForm} className="form-creation-form">
          <div className="form-group">
            <label>Form Title</label>
            <input type="text" value={newForm.title} onChange={e => setNewForm({ ...newForm, title: e.target.value })} required />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea value={newForm.description} onChange={e => setNewForm({ ...newForm, description: e.target.value })} />
          </div>
          
          <h4>Questions</h4>
          {newForm.questions.map((q, index) => (
            <div key={index} className="question-item">
              <div className="form-group">
                <label>Question Text</label>
                <input 
                  type="text" 
                  name="questionText" 
                  value={q.questionText} 
                  onChange={e => handleQuestionChange(index, e)} 
                  required 
                />
              </div>
              <div className="form-group">
                <label>Question Type</label>
                <select name="questionType" value={q.questionType} onChange={e => handleQuestionChange(index, e)}>
                  <option value="text">Text Input</option>
                  <option value="rating">Rating (1-5)</option>
                  <option value="radio">Radio Buttons</option>
                  <option value="checkbox">Checkboxes</option>
                </select>
              </div>
              {(q.questionType === 'radio' || q.questionType === 'checkbox') && (
                <div className="form-group">
                  <label>Options (comma-separated)</label>
                  <input
                    type="text"
                    onChange={e => handleOptionsChange(index, e)}
                  />
                </div>
              )}
            </div>
          ))}
          <button type="button" onClick={handleAddQuestion} className="btn btn-add-question">Add Question</button>
          <button type="submit" className="btn btn-primary">Save Form</button>
        </form>
      </div>

      <hr />

      <div className="dashboard-section">
        <h3>My Forms</h3>
        <ul className="admin-forms-list">
          {forms.map(form => (
            <li key={form._id}>
              {form.title}
              <button onClick={() => setSelectedFormId(form._id)} className="btn btn-analytics">View Analytics</button>
            </li>
          ))}
        </ul>
      </div>

      {selectedFormId && <FormAnalytics formId={selectedFormId} />}
    </div>
  );
}

export default AdminDashboard;
