import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function FeedbackForm() {
  const { formId } = useParams();
  const [form, setForm] = useState(null);
  const [formData, setFormData] = useState({});
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/forms/${formId}`)
      .then(response => {
        setForm(response.data);
      })
      .catch(error => {
        console.error("Error fetching form:", error);
      });
  }, [formId]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? (prevData[name] || []).includes(value) ? prevData[name].filter(v => v !== value) : [...(prevData[name] || []), value] : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`http://localhost:5000/api/submissions/${formId}`, {
        ...formData
      });
      setSubmitted(true);
    } catch (error) {
      console.error("Error submitting form:", error);
      alert('Submission failed. Please try again.');
    }
  };

  if (!form) return <div>Loading form...</div>;
  if (submitted) return <div className="container"><h2>Thank you for your feedback!</h2></div>;

  return (
    <div className="container">
      <h2>{form.title}</h2>
      <p>{form.description}</p>
      <form onSubmit={handleSubmit} className="feedback-form">
        {form.questions.map((q, index) => (
          <div key={index} className="form-group">
            <label>{q.questionText}</label>
            {q.questionType === 'text' && (
              <input 
                type="text" 
                name={`q${index}`} 
                onChange={handleChange} 
                required 
              />
            )}
            {q.questionType === 'rating' && (
              <input 
                type="number" 
                name={`q${index}`} 
                min="1" 
                max="5" 
                onChange={handleChange} 
                required 
              />
            )}
            {q.questionType === 'radio' && (
              <div className="options">
                {q.options.map((option, optIndex) => (
                  <label key={optIndex}>
                    <input
                      type="radio"
                      name={`q${index}`}
                      value={option}
                      onChange={handleChange}
                      required
                    /> {option}
                  </label>
                ))}
              </div>
            )}
            {q.questionType === 'checkbox' && (
              <div className="options">
                {q.options.map((option, optIndex) => (
                  <label key={optIndex}>
                    <input
                      type="checkbox"
                      name={`q${index}`}
                      value={option}
                      onChange={handleChange}
                    /> {option}
                  </label>
                ))}
              </div>
            )}
          </div>
        ))}
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
}

export default FeedbackForm;
