import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function FormAnalytics({ formId }) {
  const [submissions, setSubmissions] = useState([]);
  const [formDetails, setFormDetails] = useState(null);

  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        const token = localStorage.getItem('token');
        const [submissionsRes, formRes] = await Promise.all([
          axios.get(`http://localhost:5000/api/submissions/${formId}`, {
            headers: { 'x-auth-token': token }
          }),
          axios.get(`http://localhost:5000/api/forms/${formId}`, {
            headers: { 'x-auth-token': token }
          })
        ]);
        setSubmissions(submissionsRes.data);
        setFormDetails(formRes.data);
      } catch (err) {
        console.error(err.response.data);
      }
    };
    fetchAnalytics();
  }, [formId]);

  if (!formDetails) return <div>Loading analytics...</div>;

  const getRatingData = () => {
    const ratingQuestion = formDetails.questions.find(q => q.questionType === 'rating');
    if (!ratingQuestion) return null;

    const ratings = submissions.map(sub => sub.responses[`q${formDetails.questions.indexOf(ratingQuestion)}`]);
    const counts = {};
    for (let i = 1; i <= 5; i++) {
      counts[i] = 0;
    }
    ratings.forEach(rating => {
      counts[rating]++;
    });

    return {
      labels: ['1 Star', '2 Stars', '3 Stars', '4 Stars', '5 Stars'],
      datasets: [
        {
          label: 'Rating Distribution',
          data: Object.values(counts),
          backgroundColor: 'rgba(54, 162, 235, 0.5)',
        },
      ],
    };
  };

  const ratingData = getRatingData();

  return (
    <div className="analytics-section">
      <h3>Analytics for "{formDetails.title}"</h3>
      <p>Total Submissions: {submissions.length}</p>

      {ratingData && (
        <div className="chart-container">
          <h4>Average Rating: {(Object.keys(ratingData.datasets[0].data).reduce((acc, key) => acc + (key + 1) * ratingData.datasets[0].data[key], 0) / submissions.length).toFixed(2)}</h4>
          <Bar data={ratingData} />
        </div>
      )}

      <h4>Comments</h4>
      <div className="comments-section">
        {submissions.map((sub, index) => (
          <div key={index} className="comment">
            <p>"{sub.responses.q0}"</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default FormAnalytics;
